// Extract and format articles
const articles = items.map((item, index) => {
  const data = item.json;
  return {
    id: index + 1,
    title: data.title || 'No title',
    description: data.description || data.content || 'No description',
    link: data.link || '',
    source: data.creator || 'Unknown',
    pubDate: data.pubDate || data.isoDate || '',
    categories: data.categories || []
  };
});

// Create formatted text for Claude
const articlesText = articles.map(article => `
Article ${article.id}:
Title: ${article.title}
Source: ${article.source}
Description: ${article.description.substring(0, 300)}...
Link: ${article.link}
---
`).join('\n');

return [{
  json: {
    articles: articles,
    articlesText: articlesText,
    totalCount: articles.length
  }
}];

// Get Groq's response
const groqResponse = items[0].json;
const aiContent = groqResponse.choices[0].message.content;

// Clean and extract JSON
let cleanJson = aiContent
  .replace(/```json\n?/g, '')
  .replace(/```\n?/g, '')
  .trim();

const jsonStart = cleanJson.indexOf('{');
const jsonEnd = cleanJson.lastIndexOf('}');

if (jsonStart !== -1 && jsonEnd !== -1) {
  cleanJson = cleanJson.substring(jsonStart, jsonEnd + 1);
}

// Parse JSON
let aiData;
try {
  aiData = JSON.parse(cleanJson);
} catch (error) {
  return [{
    json: {
      error: 'Failed to parse JSON',
      errorMessage: error.message,
      receivedContent: aiContent.substring(0, 500),
      digestIntro: 'Error processing articles',
      keyInsights: [],
      articles: [],
      totalArticles: 0
    }
  }];
}

// Get ALL articles
const allArticles = $('Code').first().json.articles || [];

// Take first 10 articles
const selectedArticles = allArticles.slice(0, 10);

// Add AI summaries to them
const articlesWithSummaries = selectedArticles.map((article, index) => {
  const idKey = String(index + 1);
  const summaryData = aiData.summaries?.[idKey];
  const scoreData = aiData.relevance_scores?.[idKey];

  return {
    ...article,
    aiSummary: typeof summaryData === 'string' 
      ? summaryData 
      : (summaryData?.text || summaryData?.summary || `Summary of ${article.title}`),
    relevanceScore: typeof scoreData === 'number'
      ? scoreData
      : (scoreData?.score || scoreData?.value || 7)
  };
});

// SORT BY RELEVANCE SCORE (DESCENDING - highest first)
articlesWithSummaries.sort((a, b) => b.relevanceScore - a.relevanceScore);

return [{
  json: {
    digestIntro: aiData.digest_intro || 'Your daily tech digest - sorted by relevance',
    keyInsights: (aiData.keyInsights || aiData.key_insights || []).map(insight => 
      typeof insight === 'string' ? insight : String(insight.text || insight.insight || insight)
    ),
    articles: articlesWithSummaries,
    totalArticles: articlesWithSummaries.length,
    generatedAt: new Date().toISOString()
  }
}];

